                  &lt;div class="info"&gt;
                      &lt;h3&gt;Serveri info:&lt;/h3&gt;
                      &lt;p&gt;&lt;strong&gt;Hostname:&lt;/strong&gt; {{ inventory_hostname }}&lt;/p&gt;
                      &lt;p&gt;&lt;strong&gt;Süsteem:&lt;/strong&gt; {{ ansible_distribution }} {{ ansible_distribution_version }}&lt;/p&gt;
                      &lt;p&gt;&lt;strong&gt;Paigaldatud:&lt;/strong&gt; {{ ansible_date_time.iso8601 }}&lt;/p&gt;
