Terraform<pre><code>resource "local_file" "important_config" {
  content  = var.config_content
  filename = "${path.module}/config/important.conf"

  lifecycle {
    prevent_destroy = true
    ignore_changes  = [content]
    create_before_destroy = true
  }
}
</code></pre>

Terraform<pre><code># Multiple environments
variable "environments" {
  type = map(object({
    debug_enabled = bool
    replicas     = number
  }))
  default = {
    dev = {
      debug_enabled = true
      replicas     = 1
    }
    staging = {
      debug_enabled = false
      replicas     = 2
    }
    prod = {
      debug_enabled = false
      replicas     = 3
    }
  }
}

resource "local_file" "env_configs" {
  for_each = var.environments

  content = jsonencode({
    environment   = each.key
    debug_enabled = each.value.debug_enabled
    replicas      = each.value.replicas
    timestamp     = timestamp()
  })
  filename = "${path.module}/configs/${each.key}.json"
}

# Conditional resource creation
resource "local_file" "debug_config" {
  count = var.environment == "dev" ? 1 : 0

  content  = "Debug configuration for development"
  filename = "${path.module}/debug.conf"
}
</code></pre>
